    backupConfig: {
        enabled: true,
        intervalHours: 20,
        retention: 10,
        s3BackupConfig: {
            accessKey: "access_key",
            bucketName: "bucket_name",
            endpoint: "endpoint",
            folder: "/folder",
            region: "region",
            secretKey: "secret_key",
        },
    },
    clusterId: "<CLUSTER_ID>",
    filename: "<FILENAME>",
});
